            // ... 如果已有, 就移除类
            if (old) {old.classList.remove('active')}

            // 当前元素添加 active 
            e.target.classList.add('active')

            // 获得对应 分类区 的 offsetTop
            // console.log(`[data-wbdivname = "${e.target.dataset.name}"]`)
